import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider, useAuth } from "./context/AuthContext";
import LoginModal from "./components/LoginModal";
import RegisterModal from "./components/RegisterModal";
import DoctorPage from "./components/DoctorPage";
import ConsultationPage from "./components/ConsultationPage";

// üîí Componente de rota protegida (s√≥ acessa se estiver logado)
function PrivateRoute({ children }) {
  const { user } = useAuth();
  return user ? children : <Navigate to="/" />;
}

function AppRoutes() {
  return (
    <Routes>
      {/* üè† Tela inicial - Login */}
      <Route path="/" element={<LoginModal />} />

      {/* üßæ Cadastro */}
      <Route path="/register" element={<RegisterModal />} />

      {/* üë®‚Äç‚öïÔ∏è P√°gina do m√©dico (s√≥ acessa se estiver logado) */}
      <Route
        path="/doctor"
        element={
          <PrivateRoute>
            <DoctorPage />
          </PrivateRoute>
        }
      />

      {/* üíä P√°gina de consulta (s√≥ acessa se estiver logado) */}
      <Route
        path="/consulta"
        element={
          <PrivateRoute>
            <ConsultationPage />
          </PrivateRoute>
        }
      />

      {/* üîÅ Caso acesse uma rota inexistente */}
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default function App() {
  return (
    <AuthProvider>
      <Router>
        <AppRoutes />
      </Router>
    </AuthProvider>
  );
}
