import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "./App.css";
import LoginModal from "./components/LoginModal";
import RegisterModal from "./components/RegisterModal";
import DoctorPage from "./components/DoctorPage";
import ConsultationPage from "./components/ConsultationPage";
import { AuthProvider, useAuth } from "./context/AuthContext";

// üîí Rota protegida ‚Äî s√≥ acessa se estiver logado
function PrivateRoute({ children }) {
  const { user } = useAuth();
  return user ? children : <Navigate to="/" />;
}

function AppContent() {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const { user } = useAuth();

  // Se o m√©dico j√° estiver logado, redireciona para a p√°gina de m√©dico
  if (user) {
    return <Navigate to="/doctor" />;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>MedGuia</h1>
      </header>

      <main className="App-main">
        <p>O sistema que ir√° ajudar em suas consultas, otimizando seu tempo.</p>
        <div className="button-container">
          <button className="btn-register" onClick={() => setShowRegister(true)}>
            Cadastrar-se
          </button>
          <button className="btn-login" onClick={() => setShowLogin(true)}>
            Login
          </button>
        </div>
      </main>

      <footer className="App-footer">
        <a href="#privacy">Privacy Policy</a>
        <a href="#terms">Terms of Service</a>
        <a href="#support">Support</a>
      </footer>

      {/* Modais */}
      {showLogin && <LoginModal onClose={() => setShowLogin(false)} />}
      {showRegister && <RegisterModal onClose={() => setShowRegister(false)} />}
    </div>
  );
}

export default function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Tela inicial */}
          <Route path="/" element={<AppContent />} />

          {/* Rotas protegidas */}
          <Route
            path="/doctor"
            element={
              <PrivateRoute>
                <DoctorPage />
              </PrivateRoute>
            }
          />

          <Route
            path="/consulta"
            element={
              <PrivateRoute>
                <ConsultationPage />
              </PrivateRoute>
            }
          />

          {/* Caso o usu√°rio tente acessar rota inexistente */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}
