import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from "react-router-dom";
import "./App.css";
import LoginModal from "./components/LoginModal";
import RegisterModal from "./components/RegisterModal";
import DoctorPage from "./components/DoctorPage";
import ConsultationPage from "./components/ConsultationPage";
import { AuthProvider, useAuth } from "./context/AuthContext";

/* üîí Rota protegida: exige que o m√©dico esteja logado */
function PrivateRoute({ children }) {
  const { user } = useAuth();
  return user ? children : <Navigate to="/" replace />;
}

/* üè† Tela inicial com login/registro */
function HomePage() {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const { user } = useAuth();
  const navigate = useNavigate();

  // se j√° estiver logado, vai para a p√°gina do m√©dico
  if (user) {
    navigate("/doctor");
    return null; // evita loop de renderiza√ß√£o
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>MedGuia</h1>
      </header>

      <main className="App-main">
        <p>O sistema que ir√° ajudar em suas consultas, otimizando seu tempo.</p>
        <div className="button-container">
          <button className="btn-register" onClick={() => setShowRegister(true)}>
            Cadastrar-se
          </button>
          <button className="btn-login" onClick={() => setShowLogin(true)}>
            Login
          </button>
        </div>
      </main>

      <footer className="App-footer">
        <a href="#privacy">Privacy Policy</a>
        <a href="#terms">Terms of Service</a>
        <a href="#support">Support</a>
      </footer>

      {/* Modais */}
      {showLogin && <LoginModal onClose={() => setShowLogin(false)} />}
      {showRegister && <RegisterModal onClose={() => setShowRegister(false)} />}
    </div>
  );
}

/* üåê Configura√ß√£o principal das rotas */
export default function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Tela inicial */}
          <Route path="/" element={<HomePage />} />

          {/* P√°gina do m√©dico (protegida) */}
          <Route
            path="/doctor"
            element={
              <PrivateRoute>
                <DoctorPage />
              </PrivateRoute>
            }
          />

          {/* P√°gina de consulta (protegida) */}
          <Route
            path="/consulta"
            element={
              <PrivateRoute>
                <ConsultationPage />
              </PrivateRoute>
            }
          />

          {/* Redireciona rotas inexistentes para / */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}
