import React, { useEffect, useState } from "react";
import "./PrescriptionModal.css";

function PrescriptionModal({ qtdMedicamentos, onClose }) {
  const [medicamentos, setMedicamentos] = useState([]);

  // Criar linhas din√¢micas quando abrir
  useEffect(() => {
    const linhas = Array.from({ length: qtdMedicamentos }, () => ({
      nome: "",
      vezesAoDia: "",
      dias: "",
    }));
    setMedicamentos(linhas);
  }, [qtdMedicamentos]);

  const handleChange = (index, field, value) => {
    const updated = [...medicamentos];
    updated[index][field] = value;
    setMedicamentos(updated);
  };

const { jsPDF } = require("jspdf");
require("jspdf-autotable");

  // üìÑ Fun√ß√£o de gerar PDF REAL com jsPDF
  const gerarPDF = () => {
    const doc = new jsPDF();

    doc.setFontSize(20);
    doc.text("MedGuia", 105, 15, { align: "center" });

    doc.setFontSize(13);
    doc.text("Receita M√©dica", 105, 28, { align: "center" });

    // Tabela com jsPDF autotable
    const tabela = medicamentos.map((m, i) => [
      m.nome || "-",
      m.vezesAoDia || "-",
      m.dias || "-",
    ]);

    doc.autoTable({
      startY: 40,
      head: [["Medicamento", "Vezes ao dia", "Dias"]],
      body: tabela,
      theme: "grid",
      headStyles: { fillColor: [33, 150, 243] },
    });

    // Assinatura
    const finalY = doc.lastAutoTable.finalY + 30;
    doc.line(40, finalY, 160, finalY);
    doc.text("Assinatura", 105, finalY + 7, { align: "center" });

    // Data
    const data = new Date().toLocaleDateString("pt-BR");
    doc.text(`Data: ${data}`, 40, finalY + 20);

    doc.save("receita_medica.pdf");
  };

  return (
    <div className="modal-overlay-r">
      <div className="modal-r">
        <h1>MedGuia</h1>

        <div className="med-table">
          <div className="table-header">
            <span>Medicamento</span>
            <span>Vezes ao dia</span>
            <span>Per√≠odo</span>
          </div>

          {medicamentos.map((m, index) => (
            <div key={index} className="table-row">
              <input
                type="text"
                placeholder="Nome do medicamento"
                value={m.nome}
                onChange={(e) =>
                  handleChange(index, "nome", e.target.value)
                }
              />

              <input
                type="number"
                placeholder="Vezes ao dia"
                value={m.vezesAoDia}
                onChange={(e) =>
                  handleChange(index, "vezesAoDia", e.target.value)
                }
              />

              <input
                type="number"
                placeholder="Dias"
                value={m.dias}
                onChange={(e) =>
                  handleChange(index, "dias", e.target.value)
                }
              />
            </div>
          ))}
        </div>

        <div className="modal-footer-r">
          <button className="btn-download" onClick={gerarPDF}>
            Baixar PDF
          </button>

          <button className="btn-close" onClick={onClose}>
            Fechar
          </button>
        </div>
      </div>
    </div>
  );
}

export default PrescriptionModal;
