import React, { createContext, useContext, useState, useEffect } from "react";
import AuthService from "../services/AuthService"; // serviço que conversa com o backend

// Cria o contexto
const AuthContext = createContext();

// Provider — engloba o app inteiro
export function AuthProvider({ children }) {
  const [user, setUser] = useState(null); // médico logado
  const [loading, setLoading] = useState(true);

  // Ao abrir o app, tenta recuperar o médico do localStorage
  useEffect(() => {
    const savedUser = AuthService.getUser();
    if (savedUser) {
      setUser(savedUser);
    }
    setLoading(false);
  }, []);

  // Função de login
  const login = async (crm, senha) => {
    try {
      const loggedUser = await AuthService.login(crm, senha);
      setUser(loggedUser);
      return loggedUser;
    } catch (error) {
      throw error;
    }
  };

  // Função de logout
  const logout = () => {
    AuthService.logout();
    setUser(null);
  };

  // O provider “injeta” os dados e funções para o app inteiro
  return (
    <AuthContext.Provider value={{ user, login, logout, loading }}>
      {!loading && children}
    </AuthContext.Provider>
  );
}

// Hook para acessar o contexto facilmente
export function useAuth() {
  return useContext(AuthContext);
}
