import React, { createContext, useState, useContext } from 'react';
import AuthService from '../services/AuthService';

const AuthContext = createContext();

export function AuthProvider({ children, authService = AuthService }) {
  const [user, setUser] = useState(authService.getUser());

  const login = (email, senha) => {
    const loggedUser = authService.login(email, senha);
    setUser(loggedUser);
    return loggedUser;
  };

  const register = (nome, email, senha, confirmarSenha) => {
    const newUser = authService.register(nome, email, senha, confirmarSenha);
    setUser(newUser);
    return newUser;
  };

  const logout = () => {
    authService.logout();
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
