import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import ConsultationService from "../services/ConsultationService";
import "./DoctorPage.css";

function DoctorPage() {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [consultations, setConsultations] = useState([]);

  // üîπ Fun√ß√£o que redireciona para a p√°gina de consulta
  const handleIniciarConsulta = () => {
    navigate("/consulta");
  };

  // üîπ Busca as consultas no backend
  useEffect(() => {
    async function fetchConsultations() {
      try {
        const data = await ConsultationService.getAllConsultations();
        setConsultations(data);
      } catch (error) {
        console.error("Erro ao buscar consultas:", error);
      }
    }
    fetchConsultations();
  }, []);

  return (
    <div className="doctor-page-container">
      {/* Header */}
      <header className="doctor-page-header">
        <h1>MedGuia</h1>
      </header>

      {/* Welcome Section */}
      <section className="doctor-page-welcome">
        <h2>Bem-vindo, Dr. {user?.nome}!</h2>
        <button className="btn-start" onClick={handleIniciarConsulta}>
          Iniciar Consulta
        </button>
      </section>

      {/* Consultations List */}
      <section className="doctor-page-consultations">
        <h3>Consultas</h3>
        <div className="consultation-list">
          {consultations.length === 0 ? (
            <p>Nenhuma consulta registrada.</p>
          ) : (
            consultations.map((c, index) => (
              <div key={index} className="consultation-card">
                <div>
                  <strong>Paciente:</strong> {c.nome} <br />
                  <strong>CPF:</strong> {c.patient_cpf} <br />
                  <strong>Idade:</strong> {c.idade} <br />
                  <strong>Contato:</strong> {c.numero} <br />
                  <strong>Email:</strong> {c.email}
                </div>
                <div>
                  <strong>Medica√ß√£o:</strong> {c.medicacao}
                </div>
              </div>
            ))
          )}
        </div>
      </section>
    </div>
  );
}

export default DoctorPage;
