import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import ConsultationService from "../services/ConsultationService";
import ConsultationPage from "./ConsultationPage";
import "./DoctorPage.css";

function DoctorPage() {
  const { user, logout } = useAuth();   // ⬅️ importa o logout
  const [consultations, setConsultations] = useState([]);
  const [inConsultation, setInConsultation] = useState(false);

  useEffect(() => {
  async function loadConsultas() {
    const data = await ConsultationService.getConsultations();
    setConsultations(data);
  }

  loadConsultas();
}, []);


  if (inConsultation) {
    return <ConsultationPage onBack={() => setInConsultation(false)} />;
  }

  return (
    <div className="doctor-page-container">
      <header className="doctor-page-header">
        <h1>MedGuia</h1>
        <button className="btn-logout" onClick={logout}>
          Logout
        </button>
      </header>

      <section className="doctor-page-welcome">
        <h2>Bem-vindo, Dr. {user?.nome}!</h2>
        <button className="btn-start" onClick={() => setInConsultation(true)}>
          Iniciar Consulta
        </button>
      </section>

      <section className="consultas-section">
  <h1>Consultas</h1>

  {consultations.map((c) => (
    <div className="consulta-row" key={c.id}>
      <input
        type="text"
        value={c.nome}
        readOnly
      />
      <input
        type="text"
        value={new Date(c.data_consulta).toLocaleDateString("pt-BR")}
        readOnly
      />
    </div>
  ))}
</section>

    </div>
  );
}

export default DoctorPage;
